require(ggplot2)
library(scales)
blank_theme <- theme_minimal()+
  theme(
  axis.title.x = element_blank(),
  axis.title.y = element_blank(),
  panel.border = element_blank(),
  panel.grid=element_blank(),
  axis.ticks = element_blank(),
  plot.title=element_text(size=14, face="bold")
  )
PIE <- read.csv("values_for_r_pie_chart_NRAS_0.csv")
bp<- ggplot(PIE, aes(x="", y=Percentage, fill=Pathway))+geom_bar(width = 1, stat = "identity")
pie <- bp + coord_polar("y", start=0)+ theme(legend.text = element_text(colour="black", size = 14, face = "bold"),legend.title = element_text(colour="black", size=14, face="bold"))+  blank_theme +theme(axis.text.x=element_blank())+theme(legend.position="bottom") +guides(fill=guide_legend(nrow=4))
#png(file="pie_chart_pathways_0.png",width=300,height=300,units="px", bg="transparent")
ggsave(file="pie_chart_pathways_NRAS_0.png",bg = "transparent", scale=1)
