bp<- ggplot(PIE, aes(x="", y=Percentage, fill=Pathway))+geom_bar(width = 1, stat = "identity")
pie <- bp + coord_polar("y", start=0)+ theme(legend.text = element_text(colour="black", size = 14, face = "bold"),legend.title = element_text(colour="black", size=14, face="bold"))+  blank_theme +theme(axis.text.x=element_blank())+theme(legend.position="bottom") +guides(fill=guide_legend(nrow=4))
#png(file="pie_chart_pathways_34.png",width=300,height=300,units="px", bg="transparent")
ggsave(file="pie_chart_pathways_SPRED1_34.png",bg = "transparent", scale=1)
